package org.usfirst.frc5293.subsystems;

import edu.wpi.first.wpilibj.CANTalon;
import edu.wpi.first.wpilibj.DigitalInput;
import edu.wpi.first.wpilibj.SpeedController;
import edu.wpi.first.wpilibj.command.Subsystem;
import org.usfirst.frc5293.Devices;
import org.usfirst.frc5293.Prefs;
import org.usfirst.frc5293.commands.teleop.control.ToteElevatorControl;

public class ToteElevator extends Subsystem {

    private final DigitalInput bottomLimitSwitch = Devices.getToteElevator().getBottomLimitSwitch();
    private final SpeedController master = Devices.getToteElevator().getController();

    public void initDefaultCommand() {
        setDefaultCommand(new ToteElevatorControl());
    }

    public void raise(double percentage) {
    	setPower(percentage * -getSpeed());
    }

    public void lower(double percentage) {
        if (isBottomLimitSwitchPressed()) {
            stopImmediately();
            return;
        }
    	setPower(percentage * getSpeed());
    }

    private void setPower(double value) {
        enableVoltageRamp();
        master.set(value);
        // SmartDashboard.putNumber("tote_elevator:last_power", value);
    }

    private void enableVoltageRamp() {
        setVoltageRampRate(Prefs.getToteElevator().getVoltageRamp().get());
    }

    private void disableVoltageRamp() {
        setVoltageRampRate(0);
    }

    private void setVoltageRampRate(double rate) {
        if (master instanceof CANTalon
                && Prefs.getToteElevator().getIsVoltageRampEnabled().get()) {
            ((CANTalon) master).setVoltageRampRate(rate);
        }
    }

    public void stop() {
    	master.set(0);
    }

    public void stopImmediately() {
        disableVoltageRamp();
        stop();
    }

    private Double getSpeed() {
        return Prefs.getToteElevator().getSpeed().get();
    }

    private boolean isBottomLimitSwitchPressed() {
        return !bottomLimitSwitch.get();
    }
}

